function norm(u){
  const s = String(u || '').trim();
  // remove query/hash and trailing slashes, keep scheme+host+path
  const noQ = s.replace(/[?#].*$/, '');
  const trimmed = noQ.replace(/\/+$/, '');
  return trimmed;
}

return items.map((it, idx) => {
  const j = it.json;
  const raw = (j.URL ?? '').toString().trim();
  j.URL = raw;                 // keep original URL
  j.URL_key = norm(raw).toLowerCase(); // normalized join key
  j.__row = idx;               // (optional) original index
  return it;
});

