(() => {
  // Grab all <p> texts
  const paras = Array.isArray($json.p) ? $json.p : [$json.p || ""];

  // 1) normalize whitespace
  let cleaned = paras
    .map(s => String(s).replace(/\s+/g, " ").trim())
    .filter(Boolean);

  // 2) drop boilerplate / nav / legal / auth prompts
  const stop = /(sign\s*(in|up)|share|listen|¬©|copyright|cookie|privacy|terms?|help|about|status|press|blog|careers|subscribe|telephone|phone|address|newsletter)/i;
  cleaned = cleaned.filter(s => !stop.test(s));

  // 3) drop very short fragments unless they are the first couple
  cleaned = cleaned.filter((s, i) => s.length > 40 || i < 2);

  // 4) dedupe near-duplicates
  const seen = new Set();
  cleaned = cleaned.filter(s => {
    const k = s.toLowerCase();
    if (seen.has(k)) return false;
    seen.add(k);
    return true;
  });

  // 5) keep only first N paragraphs and cap total chars
  const maxParas = 6;      // <-- tune if you want fewer/more paragraphs
  const maxChars = 1200;   // <-- tune overall length (e.g., 800‚Äì1500)

  let selected = cleaned.slice(0, maxParas);
  let text = selected.join(" | ");

  if (text.length > maxChars) {
    text = text.slice(0, maxChars);
    const cut = Math.max(text.lastIndexOf(" | "), text.lastIndexOf(". "));
    text = (cut > 0 ? text.slice(0, cut) : text).replace(/[,;:.!?\s]*$/,"") + "‚Ä¶";
  }

  return text;
})()
}}

