// Input 1: originals from Normalize URL
// Input 2: scraped updates from Build Scraped Row
const originals = $input.all(0).map(i => i.json);
const updates   = $input.all(1).map(i => i.json);

const byKey = new Map(
  updates.map(u => [String(u.URL_key || '').trim(), u])
);

const out = originals.map(o => {
  const key = String(o.URL_key || '').trim();
  const u = byKey.get(key);
  if (u) {
    // keep URL; overwrite scraped fields when present
    o.URL         = o.URL || u.URL || '';
    o.Title       = u.Title_new       || o.Title       || '';
    o.Headlines   = u.Headlines_new   || o.Headlines   || '';
    o.Description = u.Description_new || o.Description || '';
  }
  return { json: o };
});

return out;
